# 调用流动性池合约的balanceOf函数来获取LP代币余额
pool_contract = w3.eth.contract(address=pool_contract_address, abi=pool_abi)
liquidity_amount = pool_contract.functions.liquidity().call()

print(f'Current Liquidity in Pool: {liquidity_amount}')

# 构建Mint交易参数
mint_params = {
    'from': your_address,
    'gas': 2000000,  # 适当设置gas限制
    'gasPrice': w3.to_wei('100', 'gwei'),  # 适当设置gas价格
    'nonce': w3.eth.get_transaction_count(your_address),
}

tickLower = 10000  # 替换为实际的int24值
tickUpper = 200000  # 替换为实际的int24值
amount = 10000  # 替换为实际的uint128值
  # 替换为实际的uint128值

# 构建Mint交易
mint_transaction = pool_contract.functions.mint(
    your_address,  # 你的地址
    tickLower,  # 选择的tick范围下限
    tickUpper,  # 选择的tick范围上限
    amount,  # 你要提供的token0数量
    b''
).build_transaction(mint_params)

initial_liquidity = pool_contract.functions.liquidity().call()
print(f'Initial Liquidity in Pool: {initial_liquidity}')
# 签署交易
signed_transaction = w3.eth.account.sign_transaction(mint_transaction, private_key=your_private_key)

# 发送Mint交易
tx_hash = w3.eth.send_raw_transaction(signed_transaction.rawTransaction)

print(f'Mint Transaction Hash: {tx_hash.hex()}')
# 等待交易确认
receipt = w3.eth.wait_for_transaction_receipt(tx_hash)
if receipt.status == 1:
    print('Mint Transaction Successful')
else:
    print('Mint Transaction Failed')

# 打印Mint后的流动性余额
final_liquidity = pool_contract.functions.liquidity().call()
print(f'Final Liquidity in Pool: {final_liquidity}')