# 智能合约地址
contract_address = '0xC36442b4a4522E871399CD717aBDD847Ab11FE88'

# 您的私钥
private_key = '0xac0974bec39a17e36ba4a6b4d238ff944bacb478cbed5efcae784d7bf4f2ff80'

# 构建智能合约对象
contract = w3.eth.contract(address=contract_address, abi=contract_abi)

def get_and_print_account_info(address):
    balance = w3.eth.get_balance(address)
    print(f"Account Balance: {balance} WEI")

# 设置交易参数
account_address = Web3.to_checksum_address("0xf39Fd6e51aad88F6F4ce6aB8827279cffFb92266")
# 打印 mint 操作前的账户信息
get_and_print_account_info(account_address)

account = w3.eth.account.from_key(private_key)
nonce = w3.eth.get_transaction_count(account.address)
gas_price = w3.to_wei('50', 'gwei')  # 替换为您希望的 gas 价格
gas_limit = 200000  # 替换为您希望的 gas 限制

# 构建 mint 函数的参数
token0 = Web3.to_checksum_address("0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48")
token1 = Web3.to_checksum_address("0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2")
fee = 500
tickLower = -6000
tickUpper = 6000
amount0Desired = int(1.6477261028E-8 * 10**6)  # USDC数量转换为合约单位
amount1Desired = int(0.01 * 10**18)            # WETH数量转换为合约单位
amount0Min = 0        # 设置最小接受数量为期望值的90%
amount1Min = 0 
recipient = Web3.to_checksum_address("0xf39Fd6e51aad88F6F4ce6aB8827279cffFb92266")
deadline = int(w3.eth.get_block('latest').timestamp) + 600  # 设置截止时间

# 构建交易
transaction = contract.functions.mint((
    token0, token1, fee, tickLower, tickUpper,
    amount0Desired, amount1Desired, amount0Min, amount1Min,
    recipient, deadline)
).build_transaction({
    'from': account_address,
    'gas': gas_limit,
    'gasPrice': gas_price,
    'nonce': nonce,
})

# 签署交易
signed_transaction = w3.eth.account.sign_transaction(transaction, private_key=private_key)

# 发送交易
tx_hash = w3.eth.send_raw_transaction(signed_transaction.rawTransaction)

print(f'Transaction Hash: {tx_hash.hex()}')

receipt = w3.eth.wait_for_transaction_receipt(tx_hash)

for log in receipt.logs:
    print(log)


print("Transaction status:", receipt.status)
print("Gas used:", receipt.gasUsed)
print("Cumulative gas used:", receipt.cumulativeGasUsed)
