def swap_eth_for_btc():
    amount_eth = web3.to_wei(0.1, 'ether')  # 交换的 ETH 数量
    amount_out_min = 0  # 最小接收的 BTC 数量
    path = path = [weth_contract_address, btc_address]
    to_address = my_account.address
    deadline = int(time.time()) + 900

    tx = uniswap_contract.functions.swapExactETHForTokens(
        amount_out_min,
        path,
        to_address,
        deadline
    ).build_transaction({
        'from': my_account.address,
        'value': amount_eth,
        'gas': 2000000,
        'gasPrice': web3.to_wei('50', 'gwei'),
        'nonce': web3.eth.get_transaction_count(my_account.address),
    })

    signed_tx = my_account.sign_transaction(tx)
    tx_hash = web3.eth.send_raw_transaction(signed_tx.rawTransaction)
    print(f"交易已发送，交易哈希：{tx_hash.hex()}")

    tx_receipt = web3.eth.wait_for_transaction_receipt(tx_hash)
    print(f"交易已确认，区块号：{tx_receipt.blockNumber}")

    print("Swap 交易后的账户信息:")
    print_account_info(web3, my_account.address, btc_contract)

swap_eth_for_btc()
