# Create contract instance
pool_contract = web3.eth.contract(address=pool_address, abi=pool_abi)

# Burn function parameters
tick_lower = -60000  
tick_upper = 60000   
amount = 500000
# 估算燃气费用
try:
    estimated_gas = pool_contract.functions.burn(tick_lower, tick_upper, amount).estimate_gas({
        'from': my_account.address
    })
    print("估算的燃气量:", estimated_gas)
except Exception as e:
    print("Error estimating gas:", e)
    exit(1)

# Create the transaction
burn_txn = pool_contract.functions.burn(tick_lower, tick_upper, amount).build_transaction({
    'from': my_account.address,
    'nonce': web3.eth.get_transaction_count(my_account.address),
    'gas': estimated_gas,
    'gasPrice': web3.to_wei('30', 'gwei')
})

# Sign the transaction
signed_txn = my_account.sign_transaction(burn_txn)

# Send the transaction
tx_hash = web3.eth.send_raw_transaction(signed_txn.rawTransaction)
print(f"Transaction hash: {tx_hash.hex()}")

# Wait for the transaction to be mined
tx_receipt = web3.eth.wait_for_transaction_receipt(tx_hash)
print(tx_receipt)
