from web3 import Web3

# 连接到您的本地 Ethereum 节点
node_url = "http://127.0.0.1:2024"
web3 = Web3(Web3.HTTPProvider(node_url))

# 检查是否成功连接到节点
if not web3.is_connected():
    raise Exception("无法连接到 Ethereum 节点。")

# 您的 Ethereum 地址（用于测试）
eth_address = "0xf39Fd6e51aad88F6F4ce6aB8827279cffFb92266"

# 检查 Ethereum 地址的余额
eth_balance = web3.eth.get_balance(eth_address)
print(f"ETH 余额: {web3.from_wei(eth_balance, 'ether')} ETH")

